package dsa.subarray;

public class AmazingSubArrays {

    // https://www.scaler.com/academy/mentee-dashboard/class/25589/homework/problems/1054?navref=cl_tt_lst_nm

    /*
     * You are given a string S, and you have to find all the amazing substrings of
     * S.
     * 
     * An amazing Substring is one that starts with a vowel (a, e, i, o, u, A, E, I,
     * O, U).
     */

    public static void main(String[] args) {
        System.out.println(solve("ABECa"));
        // 

    }

    static int solve(String A) {

        int idx = 0;
        int totalSubstring = 0;

        while (idx < A.length()) {
            String sub = A.substring(idx, idx + 1);
            switch (sub) {
                case "A":
                case "E":
                case "I":
                case "O":
                case "U":
                case "a":
                case "e":
                case "i":
                case "o":
                case "u": {
                    int len = A.length() - idx;
                    totalSubstring = totalSubstring + len;
                }
            }
            idx++;
        }

        return totalSubstring % 10003;
    }
}
