package subarray_problems;

public class AmazingSubArrays {

    // https://www.scaler.com/academy/mentee-dashboard/class/25589/homework/problems/1054?navref=cl_tt_lst_nm

    /*
     * You are given a string S, and you have to find all the amazing substrings of
     * S.
     * 
     * An amazing Substring is one that starts with a vowel (a, e, i, o, u, A, E, I,
     * O, U).
     */

    public static void main(String[] args) {
        System.out.println(solve(
                ""));
        // 

    }

    static int solve(String A) {
        String regex = "[aeiouAEIOU]";
        boolean start = false;
        int spCharCnt = 0;
        int ans = 0;
        for (String s : A.split("")) {
            if (!s.matches(regex) && start) {
                ans += spCharCnt;
            }
            if (s.matches(regex)) {
                start = true;
                spCharCnt++;
                ans += spCharCnt;
            }
        }

        return ans % 10003;
    }

}
